import React, { useState } from 'react';
import { matchProjectsWithJD, getKeywordsFromJD } from '../services/api';
import { Loader2, Zap, KeyRound } from 'lucide-react';

const ProjectMatcher = ({ jobDescription, setJobDescription, matchedProjects, setMatchedProjects, projects, onNext, keywords, setKeywords }) => {
    const [isMatching, setIsMatching] = useState(false);
    const [isFetchingKeywords, setIsFetchingKeywords] = useState(false);

    const handleMatch = async () => {
        if (!jobDescription) return;
        setIsMatching(true);
        try {
            const results = await matchProjectsWithJD(jobDescription, projects);
            setMatchedProjects(results);
        } catch (error) {
            alert(`AI Matching Failed: ${error.message}`);
            setMatchedProjects([]);
        } finally {
            setIsMatching(false);
        }
    };

    const handleGetKeywords = async () => {
        if (!jobDescription) return;
        setIsFetchingKeywords(true);
        try {
            const results = await getKeywordsFromJD(jobDescription);
            setKeywords(results);
        } catch (error) {
            alert(`AI Keyword Extraction Failed: ${error.message}`);
        } finally {
            setIsFetchingKeywords(false);
        }
    };

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-gray-800 mb-6 border-b pb-2">2. Smart Project Match</h2>

            <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Paste Job Description (JD)</label>
                <textarea
                    value={jobDescription}
                    onChange={(e) => setJobDescription(e.target.value)}
                    placeholder="Paste the full job description here..."
                    rows={10}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 p-3 border"
                />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <button
                    onClick={handleGetKeywords}
                    disabled={isFetchingKeywords || !jobDescription}
                    className="w-full py-3 px-4 rounded-xl font-bold transition duration-200 flex items-center justify-center bg-teal-600 hover:bg-teal-700 text-white shadow-md disabled:bg-gray-400"
                >
                    {isFetchingKeywords ? <><Loader2 className="w-5 h-5 mr-2 animate-spin" />Analyzing...</> : <><KeyRound className="w-5 h-5 mr-2" />Get Resume Keywords</>}
                </button>
                <button
                    onClick={handleMatch}
                    disabled={isMatching || !jobDescription || projects.length === 0}
                    className={`w-full py-3 px-4 rounded-xl font-bold transition duration-200 flex items-center justify-center ${
                        isMatching 
                            ? 'bg-gray-400 text-white cursor-not-allowed' 
                            : 'bg-indigo-600 hover:bg-indigo-700 text-white shadow-md disabled:bg-gray-400'
                    }`}
                >
                    {isMatching ? (
                        <>
                            <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                            Matching Projects...
                        </>
                    ) : (
                        <>
                            <Zap className="w-5 h-5 mr-2" />
                            Match Top 3 Projects
                        </>
                    )}
                </button>
            </div>
            {projects.length === 0 && <p className="text-center text-sm text-red-600 mt-2">Please fetch your GitHub projects in Step 1 before matching.</p>}

            {keywords.length > 0 && (
                <div className="mt-8">
                    <h3 className="text-xl font-semibold text-gray-700 mb-4">🎯 AI Suggested Keywords</h3>
                    <p className="text-sm text-gray-600 mb-3">Weave these keywords into your summary and experience descriptions to improve your ATS score.</p>
                    <div className="flex flex-wrap gap-2">
                        {keywords.map(keyword => (
                            <span key={keyword} className="bg-teal-100 text-teal-800 text-xs font-medium px-2.5 py-1 rounded-full">
                                {keyword}
                            </span>
                        ))}
                    </div>
                </div>
            )}

            {matchedProjects.length > 0 && (
                <div className="mt-8">
                    <h3 className="text-xl font-semibold text-gray-700 mb-4">✨ AI Recommended Projects</h3>
                    <div className="space-y-4">
                        {matchedProjects.map((p, index) => (
                            <div key={p.id} className="p-4 bg-indigo-50 rounded-lg border border-indigo-200 shadow-sm">
                                <h4 className="font-bold text-indigo-800 flex items-center">
                                    {index + 1}. {p.name}
                                </h4>
                                <p className="text-sm text-gray-600 mt-1 mb-2">
                                    **Tech Stack:** {p.tech_stack.join(', ')}
                                </p>
                                <div className="p-3 mt-2 bg-white border border-green-200 rounded-md">
                                    <p className="text-sm font-medium text-green-700">
                                        **Relevance Justification:** {p.justification}
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>
                    
                    <button onClick={onNext} className="mt-8 w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-xl shadow-md transition duration-200">
                        Review Resume with Selected Projects
                    </button>
                </div>
            )}
        </div>
    );
};

export default ProjectMatcher;
