import React, { useState, useEffect, useCallback, useMemo } from 'react';

// Firebase Imports
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, doc, setDoc, onSnapshot, serverTimestamp } from 'firebase/firestore';

// Google Gemini AI Imports
import { GoogleGenerativeAI, HarmCategory, HarmBlockThreshold } from '@google/generative-ai';

// --- 1. CONFIGURATION & INITIAL DATA ---

const GEMINI_API_KEY = process.env.REACT_APP_GEMINI_API_KEY;
const APP_ID = process.env.REACT_APP_ID;

const firebaseConfig = {
    apiKey: process.env.REACT_APP_FIREBASE_API_KEY,
    authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN,
    projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID,
    storageBucket: process.env.REACT_APP_FIREBASE_STORAGE_BUCKET,
    messagingSenderId: process.env.REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
    appId: process.env.REACT_APP_FIREBASE_APP_ID,
};

const defaultUserData = {
    name: 'Your Name',
    title: 'Aspiring Software Engineer',
    email: 'your.email@example.com',
    linkedin: 'linkedin.com/in/yourprofile',
    github: 'github.com/yourusername',
    summary: 'A brief professional summary about your skills, experience, and career goals. This section should be a concise elevator pitch.',
    experience: [
        { id: 1, title: 'Software Engineer', company: 'Tech Solutions Inc.', period: 'Jan 2022 - Present', description: 'Developed and maintained web applications using React and Node.js.' },
    ],
    education: [
        { id: 1, degree: 'B.S. in Computer Science', school: 'University of Technology', period: '2018 - 2022' },
    ],
};

const mockProjects = [
    { id: 'proj-1', name: 'E-commerce Platform', description: 'A full-stack e-commerce website with features like product catalog, shopping cart, user authentication, and a Stripe-powered checkout process.', tech_stack: ['React', 'Node.js', 'Express', 'MongoDB', 'Stripe API'], repo_url: 'https://github.com/yourusername/ecommerce' },
    { id: 'proj-2', name: 'Real-time Chat Application', description: 'A web-based chat app using WebSockets for instant messaging, supporting multiple rooms and online user status.', tech_stack: ['React', 'Socket.IO', 'Node.js', 'Redis'], repo_url: 'https://github.com/yourusername/chat-app' },
    { id: 'proj-3', name: 'Data Visualization Dashboard', description: 'An analytics dashboard that fetches data from a public API and displays it using interactive charts and graphs.', tech_stack: ['Vue.js', 'D3.js', 'Python', 'Flask', 'REST API'], repo_url: 'https://github.com/yourusername/data-dashboard' },
    { id: 'proj-4', name: 'Cloud-based File Storage Service', description: 'A serverless application for uploading, storing, and retrieving files, built on AWS infrastructure.', tech_stack: ['AWS Lambda', 'S3', 'API Gateway', 'DynamoDB', 'React'], repo_url: 'https://github.com/yourusername/cloud-storage' },
];

// --- 2. CUSTOM HOOKS ---

const useFirebase = () => {
    const [user, setUser] = useState(null);
    const [db, setDb] = useState(null);

    useEffect(() => {
        if (!firebaseConfig.apiKey || !APP_ID) {
            console.error("Firebase config or App ID is missing. Check your .env file.");
            return;
        }

        try {
            const app = initializeApp(firebaseConfig);
            const auth = getAuth(app);
            setDb(getFirestore(app));

            const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
                if (currentUser) {
                    setUser(currentUser);
                } else {
                    signInAnonymously(auth).catch((error) => {
                        console.error("Anonymous sign-in failed:", error);
                    });
                }
            });

            return () => unsubscribe();
        } catch (error) {
            console.error("Firebase initialization failed:", error);
        }
    }, []);

    return { user, db, userId: user?.uid };
};

// --- 3. API & UTILITY FUNCTIONS ---

const genAI = new GoogleGenerativeAI(GEMINI_API_KEY);

async function matchProjectsWithJD(jobDescription, projects) {
    if (!GEMINI_API_KEY) {
        throw new Error("Gemini API key is not configured. Please check your .env file.");
    }

    const model = genAI.getGenerativeModel({
        model: "gemini-1.5-flash-preview-0514",
        generationConfig: {
            responseMimeType: "application/json",
        },
        safetySettings: [
            { category: HarmCategory.HARM_CATEGORY_HARASSMENT, threshold: HarmBlockThreshold.BLOCK_NONE },
            { category: HarmCategory.HARM_CATEGORY_HATE_SPEECH, threshold: HarmBlockThreshold.BLOCK_NONE },
            { category: HarmCategory.HARM_CATEGORY_SEXUALLY_EXPLICIT, threshold: HarmBlockThreshold.BLOCK_NONE },
            { category: HarmCategory.HARM_CATEGORY_DANGEROUS_CONTENT, threshold: HarmBlockThreshold.BLOCK_NONE },
        ],
    });

    const projectSchema = {
        type: "ARRAY",
        items: {
            type: "OBJECT",
            properties: {
                id: { type: "STRING" },
                name: { type: "STRING" },
                justification: { type: "STRING" },
            },
            required: ["id", "name", "justification"],
        },
    };

    const prompt = `
    Job Description:
    ---
    ${jobDescription}
    ---

    Available Projects:
    ---
    ${JSON.stringify(projects.map(p => ({ id: p.id, name: p.name, description: p.description, tech_stack: p.tech_stack })), null, 2)}
    ---

    Task:
    Analyze the Job Description and the list of Available Projects. Select the top 3 most relevant projects that best match the requirements and skills mentioned in the job description.

    For each selected project, provide a concise, tailored 1-2 sentence justification explaining *why* it is a good match. The justification should directly reference skills or technologies from the job description.

    Return the result as a JSON array of objects, conforming to the specified schema.
    `;

    const result = await model.generateContent([prompt, { "schema": projectSchema }]);
    const response = result.response;
    return JSON.parse(response.text());
}


// --- 4. UI COMPONENTS ---

const Header = ({ step, setStep }) => (
    <header className="bg-gray-800 text-white p-4 shadow-md print:hidden">
        <div className="container mx-auto flex justify-between items-center">
            <h1 className="text-2xl font-bold">AI Resume Generator</h1>
            <nav className="flex space-x-4">
                {['Profile', 'Match Projects', 'Finalize'].map((name, index) => (
                    <button
                        key={index}
                        onClick={() => setStep(index + 1)}
                        className={`px-3 py-2 rounded-md text-sm font-medium transition-colors ${step === index + 1 ? 'bg-indigo-600 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white'}`}
                    >
                        Step {index + 1}: {name}
                    </button>
                ))}
            </nav>
        </div>
    </header>
);

const ProfileForm = ({ userData, setUserData, onSave }) => {
    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prev => ({ ...prev, [name]: value }));
    };

    const handleSectionChange = (section, index, e) => {
        const { name, value } = e.target;
        const updatedSection = [...userData[section]];
        updatedSection[index] = { ...updatedSection[index], [name]: value };
        setUserData(prev => ({ ...prev, [section]: updatedSection }));
    };

    const addSectionItem = (section) => {
        const newItem = section === 'experience'
            ? { id: Date.now(), title: '', company: '', period: '', description: '' }
            : { id: Date.now(), degree: '', school: '', period: '' };
        setUserData(prev => ({ ...prev, [section]: [...prev[section], newItem] }));
    };

    const removeSectionItem = (section, index) => {
        setUserData(prev => ({ ...prev, [section]: prev[section].filter((_, i) => i !== index) }));
    };

    return (
        <div className="space-y-8">
            {/* Personal Details */}
            <div className="p-6 bg-white rounded-lg shadow">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Personal Details</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <input name="name" value={userData.name} onChange={handleChange} placeholder="Full Name" className="p-2 border rounded" />
                    <input name="title" value={userData.title} onChange={handleChange} placeholder="Professional Title" className="p-2 border rounded" />
                    <input name="email" value={userData.email} onChange={handleChange} placeholder="Email Address" className="p-2 border rounded" />
                    <input name="linkedin" value={userData.linkedin} onChange={handleChange} placeholder="LinkedIn Profile URL" className="p-2 border rounded" />
                    <input name="github" value={userData.github} onChange={handleChange} placeholder="GitHub Profile URL" className="p-2 border rounded col-span-1 md:col-span-2" />
                </div>
                <textarea name="summary" value={userData.summary} onChange={handleChange} placeholder="Professional Summary" className="mt-4 p-2 border rounded w-full h-24" />
            </div>

            {/* Experience */}
            <div className="p-6 bg-white rounded-lg shadow">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Work Experience</h3>
                {userData.experience.map((item, index) => (
                    <div key={item.id} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4 p-4 border rounded">
                        <input name="title" value={item.title} onChange={(e) => handleSectionChange('experience', index, e)} placeholder="Job Title" className="p-2 border rounded" />
                        <input name="company" value={item.company} onChange={(e) => handleSectionChange('experience', index, e)} placeholder="Company" className="p-2 border rounded" />
                        <input name="period" value={item.period} onChange={(e) => handleSectionChange('experience', index, e)} placeholder="Period (e.g., Jan 2022 - Present)" className="p-2 border rounded" />
                        <textarea name="description" value={item.description} onChange={(e) => handleSectionChange('experience', index, e)} placeholder="Job Description" className="p-2 border rounded w-full col-span-1 md:col-span-2" />
                        <button onClick={() => removeSectionItem('experience', index)} className="text-red-500 hover:text-red-700 text-sm">Remove</button>
                    </div>
                ))}
                <button onClick={() => addSectionItem('experience')} className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">Add Experience</button>
            </div>

            {/* Education */}
            <div className="p-6 bg-white rounded-lg shadow">
                <h3 className="text-xl font-semibold mb-4 text-gray-800">Education</h3>
                {userData.education.map((item, index) => (
                    <div key={item.id} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4 p-4 border rounded">
                        <input name="degree" value={item.degree} onChange={(e) => handleSectionChange('education', index, e)} placeholder="Degree" className="p-2 border rounded" />
                        <input name="school" value={item.school} onChange={(e) => handleSectionChange('education', index, e)} placeholder="School/University" className="p-2 border rounded" />
                        <input name="period" value={item.period} onChange={(e) => handleSectionChange('education', index, e)} placeholder="Period (e.g., 2018 - 2022)" className="p-2 border rounded col-span-1 md:col-span-2" />
                        <button onClick={() => removeSectionItem('education', index)} className="text-red-500 hover:text-red-700 text-sm">Remove</button>
                    </div>
                ))}
                <button onClick={() => addSectionItem('education')} className="mt-2 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">Add Education</button>
            </div>

            <div className="flex justify-end">
                <button onClick={onSave} className="px-6 py-3 bg-green-600 text-white font-bold rounded-lg shadow-lg hover:bg-green-700 transition-transform transform hover:scale-105">
                    Save Profile
                </button>
            </div>
        </div>
    );
};

const ProjectMatcher = ({ jobDescription, setJobDescription, onMatch, isLoading, matchedProjects }) => (
    <div className="p-6 bg-white rounded-lg shadow space-y-6">
        <div>
            <h3 className="text-xl font-semibold mb-2 text-gray-800">Job Description</h3>
            <p className="text-sm text-gray-600 mb-4">Paste the full job description below to find your most relevant projects.</p>
            <textarea
                value={jobDescription}
                onChange={(e) => setJobDescription(e.target.value)}
                placeholder="Paste job description here..."
                className="w-full h-48 p-3 border rounded-md focus:ring-2 focus:ring-indigo-500 transition"
            />
        </div>
        <div className="text-center">
            <button
                onClick={onMatch}
                disabled={isLoading || !jobDescription}
                className="px-8 py-3 bg-indigo-600 text-white font-bold rounded-lg shadow-lg hover:bg-indigo-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-all transform hover:scale-105"
            >
                {isLoading ? 'Analyzing...' : 'Match Projects with AI'}
            </button>
        </div>

        {isLoading && (
            <div className="flex justify-center items-center p-8">
                <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500"></div>
            </div>
        )}

        {matchedProjects.length > 0 && !isLoading && (
            <div>
                <h3 className="text-xl font-semibold mt-8 mb-4 text-gray-800">AI Recommended Projects</h3>
                <div className="space-y-4">
                    {matchedProjects.map(project => (
                        <div key={project.id} className="p-4 border border-green-200 bg-green-50 rounded-lg">
                            <h4 className="font-bold text-lg text-gray-900">{project.name}</h4>
                            <p className="mt-2 text-gray-700">
                                <span className="font-semibold text-green-800">Relevance Justification:</span> {project.justification}
                            </p>
                        </div>
                    ))}
                </div>
            </div>
        )}
    </div>
);

const ResumePreview = ({ userData, matchedProjects }) => {
    const fullResumeData = { ...userData, projects: matchedProjects };

    const downloadJSON = () => {
        const jsonString = `data:text/json;charset=utf-8,${encodeURIComponent(JSON.stringify(fullResumeData, null, 2))}`;
        const link = document.createElement("a");
        link.href = jsonString;
        link.download = "resume_data.json";
        link.click();
    };

    return (
        <>
            {/* Print-only styles */}
            <style>
                {`
                    @media print {
                        body { -webkit-print-color-adjust: exact; print-color-adjust: exact; }
                        .print-hidden { display: none; }
                        .resume-container { box-shadow: none; border: none; margin: 0; padding: 0; }
                    }
                `}
            </style>

            <div className="p-6 bg-white rounded-lg shadow">
                <div className="flex justify-between items-center mb-6 print-hidden">
                    <h3 className="text-xl font-semibold text-gray-800">Final Resume Preview</h3>
                    <div className="flex space-x-2">
                        <button onClick={downloadJSON} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Download Data (JSON)</button>
                        <button onClick={() => window.print()} className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700">Print / Save as PDF</button>
                    </div>
                </div>

                {/* A4-like container for the resume */}
                <div className="resume-container max-w-4xl mx-auto bg-white p-8 md:p-12 border border-gray-200 shadow-lg">
                    {/* Header */}
                    <header className="text-center mb-8">
                        <h1 className="text-4xl font-bold text-gray-900">{userData.name}</h1>
                        <h2 className="text-xl font-light text-indigo-600 mt-1">{userData.title}</h2>
                        <div className="flex justify-center space-x-4 mt-3 text-sm text-gray-600">
                            <span>{userData.email}</span>
                            <span>|</span>
                            <a href={`https://${userData.linkedin}`} target="_blank" rel="noopener noreferrer" className="hover:text-indigo-600">{userData.linkedin}</a>
                            <span>|</span>
                            <a href={`https://${userData.github}`} target="_blank" rel="noopener noreferrer" className="hover:text-indigo-600">{userData.github}</a>
                        </div>
                    </header>

                    {/* Main Content */}
                    <main className="space-y-8">
                        {/* Summary */}
                        <section>
                            <h3 className="text-lg font-semibold text-gray-800 border-b-2 border-gray-200 pb-1 mb-3">PROFESSIONAL SUMMARY</h3>
                            <p className="text-gray-700 text-sm">{userData.summary}</p>
                        </section>

                        {/* AI-Matched Projects */}
                        {matchedProjects.length > 0 && (
                            <section>
                                <h3 className="text-lg font-semibold text-gray-800 border-b-2 border-gray-200 pb-1 mb-3">RELEVANT PROJECTS</h3>
                                <div className="space-y-4">
                                    {matchedProjects.map(project => {
                                        const originalProject = mockProjects.find(p => p.id === project.id);
                                        return (
                                            <div key={project.id}>
                                                <h4 className="text-md font-bold text-gray-900">{project.name}</h4>
                                                <p className="text-sm italic text-gray-600 mt-1">
                                                    <span className="font-semibold">Tech Stack:</span> {originalProject?.tech_stack.join(', ')}
                                                </p>
                                                <p className="text-sm text-gray-700 mt-1">{originalProject?.description}</p>
                                                <p className="text-sm mt-1 bg-indigo-50 p-2 rounded-md border-l-4 border-indigo-400">
                                                    <span className="font-semibold text-indigo-800">Relevance to Role:</span> {project.justification}
                                                </p>
                                            </div>
                                        );
                                    })}
                                </div>
                            </section>
                        )}

                        {/* Experience */}
                        <section>
                            <h3 className="text-lg font-semibold text-gray-800 border-b-2 border-gray-200 pb-1 mb-3">WORK EXPERIENCE</h3>
                            <div className="space-y-4">
                                {userData.experience.map(exp => (
                                    <div key={exp.id}>
                                        <div className="flex justify-between items-baseline">
                                            <h4 className="text-md font-bold text-gray-900">{exp.title}</h4>
                                            <span className="text-sm font-light text-gray-600">{exp.period}</span>
                                        </div>
                                        <h5 className="text-md font-semibold text-gray-700">{exp.company}</h5>
                                        <p className="text-sm text-gray-700 mt-1">{exp.description}</p>
                                    </div>
                                ))}
                            </div>
                        </section>

                        {/* Education */}
                        <section>
                            <h3 className="text-lg font-semibold text-gray-800 border-b-2 border-gray-200 pb-1 mb-3">EDUCATION</h3>
                            <div className="space-y-2">
                                {userData.education.map(edu => (
                                    <div key={edu.id}>
                                        <div className="flex justify-between items-baseline">
                                            <h4 className="text-md font-bold text-gray-900">{edu.degree}</h4>
                                            <span className="text-sm font-light text-gray-600">{edu.period}</span>
                                        </div>
                                        <h5 className="text-md font-semibold text-gray-700">{edu.school}</h5>
                                    </div>
                                ))}
                            </div>
                        </section>
                    </main>
                </div>
            </div>
        </>
    );
};


// --- 5. MAIN APPLICATION COMPONENT ---

function App() {
    const [step, setStep] = useState(1);
    const [userData, setUserData] = useState(defaultUserData);
    const [jobDescription, setJobDescription] = useState('');
    const [matchedProjects, setMatchedProjects] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const { user, db, userId } = useFirebase();

    const dataRef = useMemo(() => {
        if (db && userId && APP_ID) {
            return doc(db, `artifacts/${APP_ID}/users/${userId}/resumeData/profile`);
        }
        return null;
    }, [db, userId]);

    // Load data from Firestore
    useEffect(() => {
        if (!dataRef) return;

        const unsubscribe = onSnapshot(dataRef, (docSnap) => {
            if (docSnap.exists()) {
                const data = docSnap.data();
                setUserData(prev => ({ ...defaultUserData, ...data.userData }));
                setJobDescription(data.jobDescription || '');
                setMatchedProjects(data.matchedProjects || []);
            } else {
                console.log("No previous data found. Using defaults.");
            }
        }, (err) => {
            console.error("Firestore snapshot error:", err);
            setError("Failed to load data from the server.");
        });

        return () => unsubscribe();
    }, [dataRef]);

    // Save data to Firestore
    const handleSave = useCallback(async () => {
        if (!dataRef) {
            setError("Cannot save data. Database connection not available.");
            return;
        }
        try {
            await setDoc(dataRef, {
                userData,
                jobDescription,
                matchedProjects,
                lastUpdated: serverTimestamp()
            }, { merge: true });
            alert("Profile saved successfully!");
        } catch (err) {
            console.error("Error saving data:", err);
            setError("Failed to save data to the server.");
            alert("Error: Could not save profile.");
        }
    }, [dataRef, userData, jobDescription, matchedProjects]);

    // Handle AI project matching
    const handleMatchProjects = async () => {
        setIsLoading(true);
        setError('');
        try {
            const results = await matchProjectsWithJD(jobDescription, mockProjects);
            setMatchedProjects(results);
        } catch (err) {
            console.error("Error matching projects:", err);
            setError(`AI matching failed: ${err.message}`);
            alert(`AI matching failed: ${err.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    // Auto-save on project match completion
    useEffect(() => {
        if (matchedProjects.length > 0 && !isLoading) {
            handleSave();
        }
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [matchedProjects]);

    const renderStep = () => {
        switch (step) {
            case 1:
                return <ProfileForm userData={userData} setUserData={setUserData} onSave={handleSave} />;
            case 2:
                return <ProjectMatcher
                    jobDescription={jobDescription}
                    setJobDescription={setJobDescription}
                    onMatch={handleMatchProjects}
                    isLoading={isLoading}
                    matchedProjects={matchedProjects}
                />;
            case 3:
                return <ResumePreview userData={userData} matchedProjects={matchedProjects} />;
            default:
                return <ProfileForm userData={userData} setUserData={setUserData} onSave={handleSave} />;
        }
    };

    if (!userId) {
        return (
            <div className="flex items-center justify-center h-screen bg-gray-100">
                <div className="text-center">
                    <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-indigo-500 mx-auto mb-4"></div>
                    <p className="text-lg text-gray-700">Connecting to service...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gray-100 font-sans">
            <Header step={step} setStep={setStep} />
            <main className="container mx-auto p-4 md:p-8">
                {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">{error}</div>}
                {renderStep()}
            </main>
        </div>
    );
};

export default App;
