import React, { useState, useEffect } from 'react';
import { fetchGitHubData } from '../services/api';
import { Loader2, Github, Briefcase } from 'lucide-react';

export const defaultUserData = {
    name: 'Your Name',
    title: 'Professional Title',
    email: 'your.email@example.com',
    phone: '555-123-4567',
    linkedin: 'linkedin.com/in/your-profile',
    github: '',
    summary: 'A brief professional summary about your skills, experience, and career goals.',
    experience: [],
    education: []
};

const ProfileManager = ({ profiles, activeProfileId, setActiveProfileId, setProfiles, setProjects, setUserData: setAppUserData, handleSaveData, setCurrentStep }) => {
    const [isSaving, setIsSaving] = useState(false);
    const [currentProfileData, setCurrentProfileData] = useState(defaultUserData);

    useEffect(() => {
        if (activeProfileId && profiles[activeProfileId]) {
            setCurrentProfileData(profiles[activeProfileId]);
        } else {
            setCurrentProfileData(defaultUserData);
        }
    }, [activeProfileId, profiles]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCurrentProfileData(prev => ({ ...prev, [name]: value }));
    };

    const handleArrayChange = (e, index, type, field) => {
        const { value } = e.target;
        setCurrentProfileData(prev => {
            const newArray = [...(prev[type] || [])];
            newArray[index] = { ...newArray[index], [field]: value };
            return { ...prev, [type]: newArray };
        });
    };

    const addField = (type) => {
        const defaultValue = type === 'experience' 
            ? { title: '', organization: '', dates: '', description: '' }
            : { title: '', organization: '', dates: '' };
        setCurrentProfileData(prev => ({ ...prev, [type]: [...(prev[type] || []), defaultValue] }));
    };

    const removeField = (type, index) => {
        setCurrentProfileData(prev => ({ ...prev, [type]: prev[type].filter((_, i) => i !== index) }));
    };

    const handleCreateNewProfile = () => {
        const newId = `profile_${Date.now()}`;
        setActiveProfileId(newId);
        setCurrentProfileData({ ...defaultUserData, id: newId, name: 'New Profile' });
    };

    const handleSelectProfile = (e) => {
        const selectedId = e.target.value;
        setActiveProfileId(selectedId);
    };

    const handleFetchGitHub = async () => {
        if (!currentProfileData.github) {
            alert("Please enter a GitHub username first.");
            return;
        }
        setIsSaving(true);
        try {
            const { profile, repos } = await fetchGitHubData(currentProfileData.github);
            const updatedProfile = { ...currentProfileData, ...profile };
            setCurrentProfileData(updatedProfile);
            setProjects(repos);
            await handleSaveData({ 
                [`profiles.${activeProfileId}`]: updatedProfile,
                projects: repos 
            });
            alert(`Successfully fetched ${repos.length} repositories for ${profile.name}.`);
        } catch (error) {
            alert(`Failed to fetch GitHub data: ${error.message}`);
        } finally {
            setIsSaving(false);
        }
    };

    const handleSaveAndContinue = async () => {
        if (!activeProfileId) {
            alert("Please create or select a profile first.");
            return;
        }
        setIsSaving(true);
        const updatedProfiles = { ...profiles, [activeProfileId]: currentProfileData };
        setProfiles(updatedProfiles);
        setAppUserData(currentProfileData); // Update app-level user data
        await handleSaveData({ profiles: updatedProfiles, activeProfileId });
        setIsSaving(false);
        setCurrentStep(2);
    };

    const Input = ({ name, label, value, onChange, placeholder = '' }) => (
        <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">{label}</label>
            <input type="text" name={name} value={value} onChange={onChange} placeholder={placeholder} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 p-2 border" />
        </div>
    );

    const TextArea = ({ name, label, value, onChange, placeholder = '' }) => (
        <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">{label}</label>
            <textarea name={name} value={value} onChange={onChange} placeholder={placeholder} rows={4} className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 p-2 border" />
        </div>
    );

    const JobItem = ({ index, type, data }) => (
        <div className="p-4 border border-gray-200 rounded-lg mb-4 bg-white shadow-sm">
            <h4 className="font-semibold text-indigo-700 mb-2">{type === 'experience' ? 'Job' : 'Degree'} #{index + 1}</h4>
            <Input label={type === 'experience' ? 'Title/Role' : 'Degree'} value={data.title || ''} onChange={(e) => handleArrayChange(e, index, type, 'title')} />
            <Input label={type === 'experience' ? 'Company' : 'Institution'} value={data.organization || ''} onChange={(e) => handleArrayChange(e, index, type, 'organization')} />
            <Input label='Dates' value={data.dates || ''} onChange={(e) => handleArrayChange(e, index, type, 'dates')} />
            {type === 'experience' && <TextArea label='Description (use bullet points)' value={data.description || ''} onChange={(e) => handleArrayChange(e, index, type, 'description')} />}
            <button type="button" onClick={() => removeField(type, index)} className="mt-2 text-sm text-red-600 hover:text-red-800 font-medium transition">Remove</button>
        </div>
    );

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-bold text-gray-800 mb-6 border-b pb-2">1. Manage Profile</h2>
            
            <div className="p-4 bg-gray-50 rounded-lg border mb-6">
                <label htmlFor="profile-select" className="block text-sm font-medium text-gray-700">Select or Create a Profile</label>
                <div className="mt-1 flex space-x-2">
                    <select id="profile-select" value={activeProfileId || ''} onChange={handleSelectProfile} className="flex-grow block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md">
                        <option value="" disabled>-- Select a Profile --</option>
                        {Object.keys(profiles).map(id => (
                            <option key={id} value={id}>{profiles[id].name}</option>
                        ))}
                    </select>
                    <button onClick={handleCreateNewProfile} className="px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700">New Profile</button>
                </div>
            </div>

            {activeProfileId && (
                <>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <Input label="Profile Name" name="name" value={currentProfileData.name || ''} onChange={handleInputChange} placeholder="e.g., 'My SWE Profile'" />
                        <Input label="Professional Title" name="title" value={currentProfileData.title || ''} onChange={handleInputChange} placeholder="Senior Software Engineer" />
                        <Input label="Email" name="email" value={currentProfileData.email || ''} onChange={handleInputChange} placeholder="your@email.com" />
                        <Input label="Phone" name="phone" value={currentProfileData.phone || ''} onChange={handleInputChange} placeholder="555-123-4567" />
                        <Input label="LinkedIn Profile" name="linkedin" value={currentProfileData.linkedin || ''} onChange={handleInputChange} placeholder="linkedin.com/in/your-profile" />
                        <div className="md:col-span-2">
                            <label className="block text-sm font-medium text-gray-700">GitHub Username</label>
                            <div className="mt-1 flex rounded-md shadow-sm">
                                <input type="text" name="github" value={currentProfileData.github || ''} onChange={handleInputChange} placeholder="your-username" className="flex-1 block w-full rounded-none rounded-l-md border-gray-300 focus:border-indigo-500 focus:ring-indigo-500 p-2 border" />
                                <button onClick={handleFetchGitHub} disabled={isSaving} className="inline-flex items-center rounded-r-md border border-l-0 border-gray-300 bg-gray-50 px-4 text-sm text-gray-700 hover:bg-gray-100 disabled:bg-gray-200">
                                    {isSaving ? <Loader2 className="w-5 h-5 animate-spin" /> : <><Github className="w-5 h-5 mr-2" /> Fetch</>}
                                </button>
                            </div>
                        </div>
                    </div>
                    <TextArea label="Professional Summary" name="summary" value={currentProfileData.summary || ''} onChange={handleInputChange} />
                    <h3 className="text-xl font-semibold text-gray-700 mt-8 mb-4 flex items-center"><Briefcase className="w-5 h-5 mr-2" /> Professional Experience</h3>
                    {(currentProfileData.experience || []).map((data, index) => <JobItem key={index} index={index} type="experience" data={data} />)}
                    <button onClick={() => addField('experience')} className="w-full bg-indigo-50 hover:bg-indigo-100 text-indigo-700 font-medium py-2 rounded-lg border border-indigo-200 transition">Add Experience</button>
                    <h3 className="text-xl font-semibold text-gray-700 mt-8 mb-4 flex items-center"><svg xmlns="http://www.w3.org/2000/svg" className="w-5 h-5 mr-2" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M4 14.899A7 7 0 1 1 15.71 8h1.79a4.5 4.5 0 0 1 2.5 8.216"></path><path d="M12 21v-8M9 18l3 3 3-3"></path></svg> Education</h3>
                    {(currentProfileData.education || []).map((data, index) => <JobItem key={index} index={index} type="education" data={data} />)}
                    <button onClick={() => addField('education')} className="w-full bg-indigo-50 hover:bg-indigo-100 text-indigo-700 font-medium py-2 rounded-lg border border-indigo-200 transition">Add Education</button>
                    <button onClick={handleSaveAndContinue} disabled={isSaving} className={`mt-8 w-full font-bold py-3 px-4 rounded-xl shadow-md transition duration-200 flex items-center justify-center ${isSaving ? 'bg-gray-400' : 'bg-green-600 hover:bg-green-700 text-white'}`}>
                        {isSaving ? <><Loader2 className="w-5 h-5 mr-2 animate-spin" /> Saving...</> : 'Save Profile & Continue'}
                    </button>
                </>
            )}
        </div>
    );
};

export default ProfileManager;
