import React from 'react';
import { Printer, Download } from 'lucide-react';

const ResumePreview = ({ userData, projects, allTechStack, jobDescription, keywords }) => {

    const handlePrint = () => {
        window.print();
    };

    const downloadJSON = () => {
        const data = {
            profile: userData,
            jobDescription: jobDescription,
            matchedProjects: projects,
            keywords: keywords,
        };
        const jsonString = JSON.stringify(data, null, 2);
        const blob = new Blob([jsonString], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `resume_data_${userData.name.toLowerCase().replace(/\s/g, '_')}.json`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
    };

    if (!userData) {
        return <div className="text-center p-8">Please select or create a profile first.</div>;
    }

    return (
        <div className="space-y-4">
            <div className="flex justify-center space-x-4 print:hidden">
                <button onClick={handlePrint} className="flex items-center bg-indigo-600 hover:bg-indigo-700 text-white font-bold py-3 px-6 rounded-xl shadow-md transition">
                    <Printer className="w-5 h-5 mr-2" /> Print/Save as PDF
                </button>
                <button onClick={downloadJSON} className="flex items-center bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold py-3 px-6 rounded-xl shadow-md transition">
                    <Download className="w-5 h-5 mr-2" /> Download All Data (JSON)
                </button>
            </div>

            <div className="resume-container p-8 bg-white max-w-4xl mx-auto shadow-2xl border border-gray-100 rounded-xl" style={{ minHeight: '297mm' }}>
                <div className="border-b-2 border-gray-300 pb-4 mb-6 text-center">
                    <h1 className="text-4xl font-bold text-gray-800">{userData.name}</h1>
                    <p className="text-lg font-medium text-indigo-600 mt-1">{userData.title}</p>
                    <div className="flex flex-wrap justify-center text-sm text-gray-600 space-x-4 mt-2">
                        <span>{userData.phone}</span>
                        <span>&bull;</span>
                        <span>{userData.email}</span>
                        <span>&bull;</span>
                        <a href={`https://${userData.linkedin}`} target="_blank" rel="noopener noreferrer" className="hover:text-indigo-600 transition">{userData.linkedin}</a>
                        <span>&bull;</span>
                        <a href={`https://github.com/${userData.github}`} target="_blank" rel="noopener noreferrer" className="hover:text-indigo-600 transition">github.com/{userData.github}</a>
                    </div>
                </div>

                <section className="mb-6">
                    <h2 className="text-lg font-bold text-gray-700 border-b-2 border-gray-200 pb-1 mb-2 uppercase tracking-wider">PROFESSIONAL SUMMARY</h2>
                    <p className="text-gray-700 text-sm">{userData.summary}</p>
                </section>

                <section className="mb-6">
                    <h2 className="text-lg font-bold text-gray-700 border-b-2 border-gray-200 pb-1 mb-2 uppercase tracking-wider">TECHNICAL SKILLS</h2>
                    <p className="text-sm text-gray-700">
                        {allTechStack.join(', ')}
                    </p>
                </section>

                {projects.length > 0 && (
                    <section className="mb-6 print-break-avoid">
                        <h2 className="text-lg font-bold text-gray-700 border-b-2 border-gray-200 pb-1 mb-2 uppercase tracking-wider">RELEVANT PROJECTS</h2>
                        {projects.map((p) => (
                            <div key={p.id} className="mb-4">
                                <div className="flex justify-between items-baseline">
                                    <h3 className="text-md font-semibold text-gray-800">{p.name}</h3>
                                    <a href={`https://${p.repo_url}`} target="_blank" rel="noopener noreferrer" className="text-xs text-indigo-600 hover:underline">{p.repo_url}</a>
                                </div>
                                <p className="text-xs font-semibold text-gray-600 mb-1">
                                    {p.tech_stack.join(' | ')}
                                </p>
                                <p className="text-sm text-gray-700 mb-1">{p.description}</p>
                                {p.justification && (
                                    <p className="text-sm text-green-800 bg-green-50 p-1 rounded">
                                        <span className="font-semibold">Justification:</span> {p.justification}
                                    </p>
                                )}
                            </div>
                        ))}
                    </section>
                )}

                <section className="mb-6">
                    <h2 className="text-lg font-bold text-gray-700 border-b-2 border-gray-200 pb-1 mb-2 uppercase tracking-wider">PROFESSIONAL EXPERIENCE</h2>
                    {(userData.experience || []).map((job, index) => (
                        <div key={index} className="mb-4">
                            <div className="flex justify-between items-baseline">
                                <h3 className="text-md font-semibold text-gray-800">{job.title}</h3>
                                <span className="text-sm text-gray-500">{job.dates}</span>
                            </div>
                            <p className="italic text-indigo-600 text-sm mb-1">{job.organization}</p>
                            <ul className="list-disc ml-5 text-gray-700 text-sm space-y-1">
                                {job.description.split('\n').filter(line => line.trim()).map((line, i) => (
                                    <li key={i}>{line.replace(/^-/, '').trim()}</li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </section>
                
                <section className="mb-6 print-break-avoid">
                    <h2 className="text-lg font-bold text-gray-700 border-b-2 border-gray-200 pb-1 mb-2 uppercase tracking-wider">EDUCATION</h2>
                    {(userData.education || []).map((edu, index) => (
                        <div key={index} className="mb-2">
                            <div className="flex justify-between items-baseline">
                                <h3 className="text-md font-semibold text-gray-800">{edu.title}</h3>
                                <span className="text-sm text-gray-500">{edu.dates}</span>
                            </div>
                            <p className="italic text-indigo-600 text-sm">{edu.organization}</p>
                        </div>
                    ))}
                </section>

            </div>
        </div>
    );
};

export default ResumePreview;
